mapper.select:
SELECT a.name, IFNULL(t1.count1, 0) value FROM weather a LEFT JOIN ( SELECT count(weather) AS count1, weather FROM daily
and pid =#{pid}
and creatMouth =#{creatMouth}
and uid=#{uid}
GROUP BY weather ) t1 ON a.id = t1.weather

SELECT RIGHT(a.dtime,6) name, IFNULL(t1.count1, 0) value FROM everyday a LEFT JOIN ( SELECT sum(attendancetody) AS count1, dtime FROM daily
and pid =#{pid}
and creatMouth =#{creatMouth}
and uid=#{uid}
GROUP BY dtime ) t1 ON a.dtime = t1.dtime
and a.creatMouth =#{creatMouth}

SELECT
a.*, (
SELECT
count(dtime)
FROM
(
SELECT
dtime
FROM
daily


        and pid =#{pid}


        and creatMouth =#{creatMouth}


        and uid=#{uid}


GROUP BY
dtime
) a
) dailyTody,
(
SELECT
count(did)
