SELECT a.did, a.uid, a.username, a.uposition, a.utelphone, a.weather, CASE WHEN a.weather = 1 THEN '晴' WHEN a.weather = 2 THEN '阴' WHEN a.weather = 3 THEN '雨' ELSE '未知' END weatherName, a.attendancetody, a.attendanceoneself, a.effectivework, CASE WHEN a.effectivework = 1 THEN '小于2小时' WHEN a.effectivework = 2 THEN '2-4小时' WHEN a.effectivework = 3 THEN '4-6小时' WHEN a.effectivework = 4 THEN '6-8小时' ELSE '大于8小时' END effectiveworkName, a.satisfactiondegree, CASE WHEN a.satisfactiondegree = 1 THEN '很不满意' WHEN a.satisfactiondegree = 2 THEN '不太满意' WHEN a.satisfactiondegree = 3 THEN '满意' WHEN a.satisfactiondegree = 4 THEN '比较满意'ELSE '非常满意'END satisfactiondegreeName, a.equipments, a.workcomment, a.abnorname, a.dunning, a.dunningpic, a.amountody, a.invoicetody, a.dpic, a.dvoideo, a.dtime, a.pid, a.creattime, a.creatMouth, b.weixin, b.pname FROM daily a LEFT JOIN project b on a.pid=b.pid
and a.creatMouth =#{creatMouth}
and a.uid=#{uid}
ORDER BY a.creattime desc
